import Card from "./components/Card";
import Header from "./components/Header";
import Wraper from "./components/Wrapper";
import card from "./card.json";


class App extends Component {
    state = {
        cards,
        score: 0,
        highscore: 0
    };

//    score counter



// img change
changeOrder = id => {
    const cards = this.state.cards.filter(card => card.id === id);
    this.setState({cards})
}



// game over

   render() {
       return(
       <Wraper>
           <Header score = {this.state.score} highscore = {this.state.highscore}>Heros Clicky Game</Header>
           {this.state.heros.map(hero =>(
               <Card
               clickCount = {this.clickCount}
               id = {cards.id}
               key ={cards.key}
               image = {cards.image}
               />
           ) )}
       </Wraper>
       )
   }
}

export default App;
